import Drinks from '../Meals/Drinks';
import Meals from '../Meals/Meals';
import classes from './Menu.module.css';
import ReactDOM, { useState } from 'react';
import React from 'react';
import Sushi from '../Meals/Sushi';
import Gelati from '../Meals/Gelati';
import MenuGranMattino from '../Meals/MenuGranMattino';
const Menu = () => {
  const [sushiOpen, setSushiOpen] = useState(false);
  const [drinksOpen, setDrinksOpen] = useState(false);
  const [iceCreamOpen, setIceCreamOpen] = useState(false);
  const [menuGranMattino, setMenuGranMattino] = useState(false);
  const handleMenuGranMattino = () => {
    setSushiOpen(false);
    setIceCreamOpen(false);
    setDrinksOpen(false);
    setMenuGranMattino(!menuGranMattino);
  };
  const handleDrinksOpen = () => {
    setSushiOpen(false);
    setIceCreamOpen(false);
    setMenuGranMattino(false);
    setDrinksOpen(!drinksOpen);
  };
  const handleIceCreamOpen = () => {
    setSushiOpen(false);
    setDrinksOpen(false);
    setMenuGranMattino(false);
    setIceCreamOpen(!iceCreamOpen);
  };
  const handleSushiClick = () => {
    setDrinksOpen(false);
    setIceCreamOpen(false);
    setMenuGranMattino(false);
    setSushiOpen(!sushiOpen);
  };
  return (
    <nav>
      <ul>
        <li onClick={handleMenuClick}>I nostri prodotti</li>
        <ul>
          <li onClick={handleSushiClick}>
            Sushi
            {sushiOpen && (
              <div id="content-wrap" style={{ float: 'right', width: '75%' }}>
                <Sushi />
              </div>
            )}
          </li>
          <li onClick={handleDrinksOpen}>
            Bevande
            {drinksOpen && (
              <div id="content-wrap" style={{ float: 'right', width: '75%' }}>
                <Drinks />
              </div>
            )}
          </li>
          <li onClick={handleIceCreamOpen}>
            Gelati e Dessert
            {iceCreamOpen && (
              <div id="content-wrap" style={{ float: 'right', width: '75%' }}>
                <Gelati />
              </div>
            )}
          </li>
          <li onClick={handleMenuGranMattino}>
            Menu Gran Mattino
            {menuGranMattino && (
              <div id="content-wrap" style={{ float: 'right', width: '75%' }}>
                <MenuGranMattino />
              </div>
            )}
          </li>
        </ul>
      </ul>
    </nav>
  );
};
export default Menu;
