import { React } from "react";
import CartContext from "./CartContext.js";
import { useReducer } from "react";
const DefaultCartState = {
  Items: [],
  TotalAmount: 0,
};
const CartReducer = (state, action) => {

  if (action.type === "ADD") {
    const UpdateState=state.Items.concat(action.item)
    const NewTotalAmount=state.TotalAmount+action.item.price*action.item.amount
    const existingItem=state.Items.findIndex(item=>item.id===action.item.id);
    return{
     Items: UpdateState,
     TotalAmount: NewTotalAmount,
    }
  }
  if (action.type === "REMOVE") {
    const UpdateState=state.item.filter(item=>{return item.id!=action.id})
    return{
     Items: UpdateState,
    }
  }
  return DefaultCartState;
};
const CartProvider = (props) => {
  const [CartState, DispatchCartAction] = useReducer(
    CartReducer,
    DefaultCartState
  );
  const AddItemHandler = (item) => {
    DispatchCartAction({ type: "ADD", item: item });
  };
  const RemoveItemHandler = (id) => {
    DispatchCartAction({ type: "REMOVE", id: id });
  };
  const cartContext = {
    Items: CartState.Items,
    TotalAmount: CartState.TotalAmount,
    AddItem: AddItemHandler,
    RemoveItem: RemoveItemHandler,
  };
  return (
    <CartContext.Provider value={cartContext}>
      {props.children}
      {console.log(cartContext.Items)}
      {console.log(cartContext.TotalAmount)}

    </CartContext.Provider>
  );
};
export default CartProvider;
