import Drinks from '../Meals/Drinks';
import Meals from '../Meals/Meals';
import classes from './Menu.module.css';
import ReactDOM, { Fragment, useState } from 'react';
import React from 'react';
import { BrowserRouter as Link } from 'react-router-dom';
import Sushi from '../Meals/Sushi';
import Gelati from '../Meals/Gelati';
const Menu = () => {
  const [menuOpen, setMenuOpen] = useState(false);
  const [sushiOpen, setSushiOpen] = useState(false);
  const [drinksOpen,setDrinksOpen]=useState(false);
  const [iceCreamOpen,setIceCreamOpen]=useState(false);
  const handleDrinksOpen=()=>{
    setSushiOpen(false);
    setDrinksOpen(!drinksOpen);
  }
  const handleIceCreamOpen=()=>{
    setSushiOpen(false);
    setOpen(!drinksOpen);
  }
  const handleSushiClick = () => {
    setDrinksOpen(false);
    setSushiOpen(!sushiOpen);
  };
  const handleMenuClick = () => {
    setMenuOpen(!menuOpen);
  };
  return (
    <nav>
      <ul>
        <li onClick={handleMenuClick}>Menu</li>
        {menuOpen && (
          <ul>
            <li onClick={handleSushiClick}>
              Sushi
              {sushiOpen && (
                <div id="content-wrap" style={{ float: 'right', width: '75%' }}>
                  <Sushi />
                </div>
              )}
            </li>
            <li onClick={handleDrinksOpen}>Bevande
              {drinksOpen&&(
                <div id="content-wrap" style={{ float: 'right', width: '75%' }}>
                <Drinks />
              </div>
              )}
            </li>
            <li onClick={handleIceCreamOpen}>Bevande
              {iceCreamOpen&&(
                <div id="content-wrap" style={{ float: 'right', width: '75%' }}>
                <Gelati />
              </div>
              )}
            </li>
          </ul>
        )}
      </ul>
    </nav>
  );
};
