import Drinks from '../Meals/Drinks';
import { Fragment, useState } from 'react';
import React from 'react';
import Sushi from '../Meals/Sushi';
import Gelati from '../Meals/Gelati';
import MenuGranMattino from '../Meals/MenuGranMattino';
import classses from './Menu.css';
const Menu = () => {
  const [sushiOpen, setSushiOpen] = useState(true);
  const [drinksOpen, setDrinksOpen] = useState(false);
  const [iceCreamOpen, setIceCreamOpen] = useState(false);
  const [menuGranMattino, setMenuGranMattino] = useState(false);
  const handleMenuGranMattino = () => {
    setSushiOpen(false);
    setIceCreamOpen(false);
    setDrinksOpen(false);
    setMenuGranMattino(true);
  };
  const handleDrinksOpen = () => {
    setSushiOpen(false);
    setIceCreamOpen(false);
    setMenuGranMattino(false);
    setDrinksOpen(true);
  };
  const handleIceCreamOpen = () => {
    setSushiOpen(false);
    setDrinksOpen(false);
    setMenuGranMattino(false);
    setIceCreamOpen(true);
  };
  const handleSushiClick = () => {
    setDrinksOpen(false);
    setIceCreamOpen(false);
    setMenuGranMattino(false);
    setSushiOpen(true);
  };
  return (
    <Fragment>
      <nav>
        <div>
          <li className="h1">I nostri prodotti</li>
          <ul className="items">
            <li onClick={handleSushiClick}>
              <span className="items-body-content">Sushi</span>
            </li>
            <li onClick={handleDrinksOpen}>
              <span className="items-body-content">Bevande</span>
            </li>
            <li onClick={handleIceCreamOpen}>
              <span className="items-body-content">Gelati e Dessert</span>
            </li>
            <li onClick={handleMenuGranMattino}>
              <span className="items-body-content">Menu Gran Mattino</span>
            </li>
          </ul>
        </div>
      </nav>
      <div>
        {sushiOpen && (
          <div id="content-wrap" style={{ float: 'right', width: '75%' }}>
            <Sushi />
          </div>
        )}
        {drinksOpen && (
          <div id="content-wrap" style={{ float: 'right', width: '75%' }}>
            <Drinks />
          </div>
        )}
        {iceCreamOpen && (
          <div id="content-wrap" style={{ float: 'right', width: '75%' }}>
            <Gelati />
          </div>
        )}
        {menuGranMattino && (
          <div id="content-wrap" style={{ float: 'right', width: '75%' }}>
            <MenuGranMattino />
          </div>
        )}
      </div>
    </Fragment>
  );
};
export default Menu;
